package com.github.rickart.softserve.task.algorithm;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class SimpleAlgorithmStrategyFactoryTest {

    @Test
    public void shortStringTest() {
        SimpleAlgorithmStrategyFactory factory = new SimpleAlgorithmStrategyFactory();
        AlgorithmStrategy strategy = factory.createStrategy("a");
        assertEquals(StreamAlgorithmStrategy.class, strategy.getClass());
    }

    @Test
    public void longStringTest() {
        String longString = "";
        SimpleAlgorithmStrategyFactory factory = new SimpleAlgorithmStrategyFactory();
        AlgorithmStrategy strategy = factory.createStrategy(longString);
        assertEquals(LinearAlgorithmStrategy.class, strategy.getClass());
    }
}